using namespace std;

const int N = 10001005;

int n, q, l, r, mid, x, sum, ma, ans[N], o, cnt, cnt1;
map<int, int> m;

// node
struct node {
    int l, r, len, lc, rc;  // lr, len, lcrc
} a[N];

// deno
struct deno {
    int x, id;  // x, id
} c[N];

// w
struct w {
    int l, r, x, id;  // lr, x, id
} b[N];

// 
void gx(int p) {
    a[p].len = a[p * 2].len + a[p * 2 + 1].len;  // 
    if (a[p * 2].rc == a[p * 2 + 1].lc)  // 1
        a[p].len--;
    a[p].lc = a[p * 2].lc;  // 
    a[p].rc = a[p * 2 + 1].rc;  // 
}

// 
void build(int p, int l, int r) {
    a[p].l = l;
    a[p].r = r;
    if (l == r) {  // 
        a[p].len = a[p].lc = a[p].rc = 1;  // 1
        return;
    }
    int mid = (l + r) / 2;
    build(p * 2, l, mid);  // 
    build(p * 2 + 1, mid + 1, r);  // 
    gx(p);  // 
}

// 
void change(int p, int l, int r) {
    if (l <= a[p].l && a[p].r <= r) {  // 
        a[p].len = 0;  // 0
        a[p].lc = a[p].rc = ++cnt1;  // 
        return;
    }
    int mid = (a[p].l + a[p].r) / 2;
    if (l <= mid) change(p * 2, l, r);  // 
    if (mid < r) change(p * 2 + 1, l, r);  // 
    gx(p);  // 
}

// 
int ask(int p, int l, int r) {
    if (l <= a[p].l && a[p].r <= r)  // 
        return a[p].len;
    int mid = (a[p].l + a[p].r) / 2;
    int ans1 = 0;
    if (l <= mid && mid < r) {  // 
        ans1 = ask(p * 2, l, r) + ask(p * 2 + 1, l, r);  // 
        if (a[p * 2].rc == a[p * 2 + 1].lc)  // 1
            ans1--;
    } else if (l <= mid) {  // 
        ans1 = ask(p * 2, l, r);
    } else if (mid < r) {  // 
        ans1 = ask(p * 2 + 1, l, r);
    }
    return ans1;
}

// 
bool cmp(w x, w y) {
    return x.x < y.x;
}

// 
bool cmp1(deno x, deno y) {
    return x.x < y.x;
}

signed main() {
