#include <bits/stdc++.h>
#define int long long
#define endl "\n"
using namespace std;

const int N = 10001005;

int n, q, l, r, mid, x, sum, ma, ans[N], o, cnt, cnt1;
map<int, int> m;

// node
struct node {
    int l, r, len, lc, rc;  // lr, len, lcrc
} a[N];

// deno
struct deno {
    int x, id;  // x, id
} c[N];

// w
struct w {
    int l, r, x, id;  // lr, x, id
} b[N];

// 
void gx(int p) {
    a[p].len = a[p * 2].len + a[p * 2 + 1].len;  // 
    if (a[p * 2].rc == a[p * 2 + 1].lc)  // 1
        a[p].len--;
    a[p].lc = a[p * 2].lc;  // 
    a[p].rc = a[p * 2 + 1].rc;  // 
}

// 
void build(int p, int l, int r) {
    a[p].l = l;
    a[p].r = r;
    if (l == r) {  // 
        a[p].len = a[p].lc = a[p].rc = 1;  // 1
        return;
    }
    int mid = (l + r) / 2;
    build(p * 2, l, mid);  // 
    build(p * 2 + 1, mid + 1, r);  // 
    gx(p);  // 
}

// 
void change(int p, int l, int r) {
    if (l <= a[p].l && a[p].r <= r) {  // 
        a[p].len = 0;  // 0
        a[p].lc = a[p].rc = ++cnt1;  // 
        return;
    }
    int mid = (a[p].l + a[p].r) / 2;
    if (l <= mid) change(p * 2, l, r);  // 
    if (mid < r) change(p * 2 + 1, l, r);  // 
    gx(p);  // 
}

// 
int ask(int p, int l, int r) {
    if (l <= a[p].l && a[p].r <= r)  // 
        return a[p].len;
    int mid = (a[p].l + a[p].r) / 2;
    int ans1 = 0;
    if (l <= mid && mid < r) {  // 
        ans1 = ask(p * 2, l, r) + ask(p * 2 + 1, l, r);  // 
        if (a[p * 2].rc == a[p * 2 + 1].lc)  // 1
            ans1--;
    } else if (l <= mid) {  // 
        ans1 = ask(p * 2, l, r);
    } else if (mid < r) {  // 
        ans1 = ask(p * 2 + 1, l, r);
    }
    return ans1;
}

// 
bool cmp(w x, w y) {
    return x.x < y.x;
}

// 
bool cmp1(deno x, deno y) {
    return x.x < y.x;
}

signed main() {
    ios::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);
    cin >> n >> q;
    cnt1 = n + 1;
    for (int i = 1; i <= n; i++) {
        cin >> c[i].x;
        c[i].id = i;
    }
    sort(c + 1, c + 1 + n, cmp1);  // 
    for (int i = 1; i <= q; i++) {
        cin >> b[i].l >> b[i].r >> b[i].x;
        b[i].id = i;
    }
    sort(b + 1, b + 1 + q, cmp);  // 
    cnt = 1;
    build(1, 1, n);  // 
    for (int i = 1; i <= q; i++) {
        while (cnt <= n && c[cnt].x <= b[i].x) {
            change(1, c[cnt].id, c[cnt].id);  // 
            cnt++;
        }
        ans[b[i].id] = ask(1, b[i].l, b[i].r);  // 
    }
    for (int i = 1; i <= q; i++) {
        cout << ans[i] << endl;  // 
    }
    return 0;
}
